    def asignar_pokemon(self):

        url = "https://pokeapi.co/api/v2/pokemon?limit=151"
        response = requests.get(url)
        data = response.json()

        nombre_pokemons = [pokemon["name"].capitalize() for pokemon in data["results"]]
        
        self.comboBox = QtWidgets.QComboBox(parent=None)
        self.comboBox.setObjectName("comboBox")
        self.comboBox.addItems(nombre_pokemons)

        self.comboBox.currentIndexChanged.connect(self.asignar_habilidad)
        
        return self.comboBox
    
    def asignar_habilidad(self):
        pokemon_seleccionado = self.comboBox.currentText().lower()
        url = f"https://pokeapi.co/api/v2/pokemon/{pokemon_seleccionado}"
        response = requests.get(url)
        data = response.json()

        habilidad_comboBox_name = "comboBox_29"
        habilidad_comboBox = getattr(self, habilidad_comboBox_name, None)

        if habilidad_comboBox:
            habilidad_comboBox.clear()

            if "abilities" in data:
                habilidad = [ability["ability"]["name"].capitalize() for ability in data["abilities"]]
                habilidad_comboBox.addItems(habilidad)

        return habilidad_comboBox
